
import NextAuth from "next-auth";

declare module "next-auth" {
  interface Session {
    user: {
      id: string;
      name?: string | null;
      email?: string | null;
      image?: string | null;
      firstName?: string | null;
      lastName?: string | null;
      role?: string;
      isApproved?: boolean;
    }
  }

  interface User {
    id: string;
    firstName?: string | null;
    lastName?: string | null;
    role?: string;
    isApproved?: boolean;
  }
}

declare module "next-auth/jwt" {
  interface JWT {
    id: string;
    firstName?: string | null;
    lastName?: string | null;
    role?: string;
    isApproved?: boolean;
  }
}
